import { useState } from "react";
import "./Display.css";

const Display = ({ contract, account }) => {
  const [loading, setLoading] = useState(false);

  const getdata = async () => {
    setLoading(true);
    const hash = document.querySelector(".address").value;
    try {
      if (!hash) {
        throw new Error("Please enter a valid IPFS hash");
      }
      
      const response = await fetch(`https://gateway.pinata.cloud/ipfs/${hash}`);
      
      if (!response.ok) {
        throw new Error(`Failed to fetch file with hash ${hash}`);
      }

      // Retrieve file metadata from Pinata
      const metadataResponse = await fetch(`https://gateway.pinata.cloud/ipfs/${hash}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'pinata_api_key': 'your_pinata_api_key',
          'pinata_secret_api_key': 'your_pinata_secret_api_key'
        }
      });
      if (!metadataResponse.ok) {
        throw new Error(`Failed to retrieve file metadata for hash ${hash}`);
      }
      const metadata = await metadataResponse.json();
      
      // Download the file
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      // Set the filename to the original filename
      a.download = metadata.name || `file_${hash}`; 
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
    } catch (error) {
      console.error(error);
      alert(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <input
        type="text"
        placeholder="Enter IPFS Hash"
        className="address"
      ></input>
      <button className="center button" onClick={getdata}>
        Download File
      </button>
      {loading && <div>Loading...</div>}
    </>
  );
};

export default Display;
